import React from "react";
import { THEMES, setTheme } from "../theme.js";
function MessageCenter() {
    return (
        <msgFrame className="border border-success p-2 mb-2" data-bs-theme="dark">
            <div className="container inner-frame p-1 col-sm-4 col-md-6 col-lg-6 float-start border bg-dark">
                <div className="row col-sm-4 col-md-6 col-lg-8">
                    {/* 12 avaliable col */}
                    <div className="col-sm-4 col-md-6 justifiy-content-center order-1">
                        <div className="message-incoming round-3p-0.5 border bg-black">OTHER</div>
                    </div>
                    <div className="col-sm-4 col-md-6 justifiy-content-center order-2">
                        <div className="message-outgoing p-0.5 border bg-dark">ME</div>
                    </div>
                </div>
                <div className="row">
                    <input type="textfield" className="chat-input"></input>
                </div>
            </div>
            
        </msgFrame>
    );
}


export default MessageCenter;
